#!/bin/python3import mathimport osimport randomimport reimport sys## Complete the 'biggerIsGreater' function below.## The function is expected to return a STRING.# The function accepts STRING w as parameter.#def biggerIsGreater(w):    if len(w) < 2:        return 'no answer'    left_side = list(w)    right_side = [left_side.pop()]    found = False    while not found:        #print(left_side, right_side)        if len(left_side) == 0:            return 'no answer'        to_compare = left_side.pop()        for (ind, char) in enumerate(right_side):            if char > to_compare:                right_side[ind] = to_compare                right_side = sorted(right_side)                left_side.append(char)                return "".join(left_side + right_side)        right_side.append(to_compare)        right_side = sorted(right_side)    return "no answer"if __name__ == '__main__':    fptr = open(os.environ['OUTPUT_PATH'], 'w')    T = int(input().strip())    for T_itr in range(T):        w = input()        result = biggerIsGreater(w)        fptr.write(result + '\n')    fptr.close()